import React, { useState, Fragment } from "react";
import PropTypes from "prop-types";

import { Container, Row, Col, Form, Button } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

import { FaSearch } from "react-icons/fa";

import getHaversineDistance from "../scripts/haversine.js";
import parse from "../scripts/parse.js";

// A search function is passed in -- eventually by the context, but for now, it is handled as a prop.
// It is destructured in the parameters, denoted by {}.
function Search({ searchFunction }) {
	// React Hook used to set text value to a constant
	const [text, setText] = useState("");

	const distances = ["Test","Test2","Test3"];

	var searchFunction = (text) => {
		var distance = distances.indexOf(text);
		return distance;
	}

	// Calls the prop.search function with text in the form.
	const onSubmit = (e) => {
		e.preventDefault();
		/*const philly = { "lat": 39.9526, "lon": -75.1652 }
		const nyc = { lat: 40.7128, lon: -74.0060 }
		const losAngeles = { "lat": 34.0522, "lon": -118.2437 }*/
		var stuff = text;

		console.log(text);

		var data = JSON.parse(stuff);
		console.log(data);
		var dis = getHaversineDistance({ lat: 39.9526, lon: -75.1652 }, data);
		var trail = parse('trails');
		console.log(trail);
		console.log(dis);

		setText("");
	};

	// Sets search bar text to current typed information
	const onChange = (e) => setText(e.target.value);

	return (
		<Fragment>
			<Container>
				<Row>
					<Col className="p-0">
						<Form onSubmit={onSubmit}>
							<Form.Control
								type="text"
								placeholder="Enter a zipcode..."
								onChange={onChange}
								value={text}
								style={{
									borderBottomRightRadius: "0",
									borderTopRightRadius: "0",
								}}
							></Form.Control>
						</Form>
					</Col>
					<Col className="p-0" md="auto">
						<Button
							variant="primary"
							onClick={onSubmit}
							style={{
								borderBottomLeftRadius: "0",
								borderTopLeftRadius: "0",
							}}
						>
							<FaSearch className="align-center mb-1"></FaSearch>
						</Button>
					</Col>
				</Row>
			</Container>
		</Fragment>
	);
}

Search.propTypes = {
	searchFunction: PropTypes.func.isRequired,
};

export default Search;
