  name = name of trail
  distance = distance from input location
  */
function TrailItem() {
    const [state, setState] = useState("");;

    const onClick = () => {
        console.log(state);
    }

    return(
        <Card as="a" onClick={onClick} style={{ cursor: "pointer" }}>
            <Card.Body>This is a clickable card.</Card.Body>
        </Card>
    );
}

export default TrailItem;
